import './styles/App.css';
import twitterLogo from './assets/twitter-logo.svg';
import React from "react";
import { ethers } from "ethers";
import myEpicNft from './utils/MyEpicNFT.json'
import loading from './loading.svg';

// Constants
const TWITTER_HANDLE = 'kng';
const TWITTER_LINK = `https://twitter.com/${TWITTER_HANDLE}`;
const CONTRACT_ADDRESS = "0xed23Fc811e3D16aEBd82f5ff6FE7D0A4f9b9f2A6";
const TOTAL_MINT_COUNT = 50;

function parseEthNetworkToStr(id) {
  const ETH_NETWORKS = {
    "1": "Mainnet",
    "42": "Kovan",
    "3": "Ropsten",
    "4": "Rinkeby",
    "5": "Goerli",
  };

  if (ETH_NETWORKS.hasOwnProperty(id)) {
    return ETH_NETWORKS[id];
  } else {
    return `Unknown network (${id})`;
  }
};

const App = () => {
  const [currentAccount, setCurrentAccount] = React.useState("");
  const [svg, setSVG] = React.useState();
  const [tokenId, setTokenId] = React.useState();
  const [isMinting, setIsMinting] = React.useState(false);
  const [ethNetwork, setEthNetwork] = React.useState();

  const OPENSEA_LINK = `https://testnets.opensea.io/assets/${CONTRACT_ADDRESS}/${tokenId}`;

  const setupEventListener = async () => {
    // Most of this looks the same as our function askContractToMintNft
    try {
      const { ethereum } = window;

      if (ethereum) {
        // Same stuff again
        const provider = new ethers.providers.Web3Provider(ethereum);
        const signer = provider.getSigner();
        const connectedContract = new ethers.Contract(CONTRACT_ADDRESS, myEpicNft.abi, signer);

        // THIS IS THE MAGIC SAUCE.
        // This will essentially "capture" our event when our contract throws it.
        // If you're familiar with webhooks, it's very similar to that!
        connectedContract.on("NewEpicNFTMinted", (from, tokenId, finalSVG) => {
          console.log(from, tokenId.toNumber())
          setSVG(finalSVG);
          setTokenId(tokenId.toNumber());
        });

        console.log("Setup event listener!")

      } else {
        console.log("Ethereum object doesn't exist!");
      }
    } catch (error) {
      console.log(error)
    }
  }
  const checkIfWalletIsConnected = () => {
    /*
    * First make sure we have access to window.ethereum
    */
    const { ethereum } = window;

    if (!ethereum) {
      console.log("Make sure you have metamask!");
      return;
    } else {
      const network = ethereum.networkVersion;
      console.log({network})
      setEthNetwork(parseEthNetworkToStr(ethereum.networkVersion));
      console.log("We have the ethereum object", ethereum, ethereum.networkVersion);
    }

    ethereum.request({ method: 'eth_accounts'})
    .then(accounts => {
      if (accounts.length) {
        const account = accounts[0];
        setCurrentAccount(account);
        console.log('Found an authorized account: ', account);
        setupEventListener();
      } else {
        console.log('No authorized account found.');
      }
    });
  }

  const connectWallet = async () => {
    try {
      const { ethereum } = window;

      if (!ethereum) {
        alert("Get MetaMask!");
        return;
      }

      const accounts = await ethereum.request({ method: "eth_requestAccounts" });

      console.log("Connected", accounts[0]);
      setCurrentAccount(accounts[0]);
      setupEventListener();
    } catch (error) {
      console.log(error)
    }
  }

  const askContractToMintNft = async () => {
    try {
      setIsMinting(true);
      const { ethereum } = window;

      if (ethereum) {
        const provider = new ethers.providers.Web3Provider(ethereum);
        const signer = provider.getSigner();
        const connectedContract = new ethers.Contract(CONTRACT_ADDRESS, myEpicNft.abi, signer);

        console.log("Going to pop wallet now to pay gas...")
        let nftTxn = await connectedContract.makeAnEpicNFT();

        console.log("Mining...please wait.")
        await nftTxn.wait();

        console.log(`Mined, see transaction: https://rinkeby.etherscan.io/tx/${nftTxn.hash}`);
      } else {
        console.log("Ethereum object doesn't exist!");
      }
      setIsMinting(false);
    } catch (error) {
      console.log(error);
      setIsMinting(false);
    }
  }

  React.useEffect(() => {
    checkIfWalletIsConnected();
  }, [])

  // Render Methods
  const renderNotConnectedContainer = () => (
    <button onClick={connectWallet} className="cta-button connect-wallet-button">
      Connect to Wallet
    </button>
  );

  const renderMintUI = () => (
    <button onClick={askContractToMintNft} className="cta-button connect-wallet-button">
      {isMinting ? <span>Minting...</span> : <span>Mint NFT</span>}
    </button>
  );

  const renderNetworkWarning = () => (
    <p className="warning">{`Hey — I see you're connected to ${ethNetwork} but this only works on Rinkeby!`}</p>
  )

  const svgUri = `data:image/svg+xml;utf8,${svg}`;
  console.log({svg})
  return (
    <div className="App">
      <div className="container">
        <div className="header-container">
          <p className="header gradient-text">My NFT Collection</p>
          <p className="sub-text">
            Each unique. Each beautiful. Discover your NFT today.
          </p>
          {currentAccount ? renderMintUI() : renderNotConnectedContainer()}
          { ethNetwork && ethNetwork !== "Rinkeby" ? renderNetworkWarning(): null}
        </div>
        { svg && !isMinting ? (
          <div className="svgContainer">
            <img width="500px" src={svgUri} alt={`NFT #${tokenId}`} />
            <p><a href={OPENSEA_LINK}>View on OpenSea</a>
          </p></div>
          ) : isMinting ? <img className="loading" src={loading} alt="Loading spinner" />: null }
        <div className="footer-container">
          <img alt="Twitter Logo" className="twitter-logo" src={twitterLogo} />
          <a
            className="footer-text"
            href={TWITTER_LINK}
            target="_blank"
            rel="noreferrer"
          >{`built by @${TWITTER_HANDLE}`}</a>
        </div>
      </div>
    </div>
  );
};

export default App;
